CREATE VIEW MED1 AS SELECT ID NUMERO_MEDICO,NOMBRE N_MEDICO,APELLIDO A_MEDICO,SALARIO SAL_MEDICO,CIUDAD FROM MEDICO WHERE CIUDAD=1;
CREATE VIEW MEDJUAN AS SELECT ID_ESPECIALIDAD ESPECIALIDAD,ID_MEDICO MEDICO FROM ESPECIALIDADXMEDICO WHERE ID_MEDICO=1;
CREATE VIEW MED2 AS SELECT MEDICO.ID ID,MEDICO.NOMBRE MED_NOMBRE,MEDICO.APELLIDO APELLIDO,CIUDAD.NOMBRE CIU_NOMBRE FROM MEDICO,CIUDAD WHERE CIUDAD.NOMBRE='BOGOTA' AND MEDICO.CIUDAD=1;
CREATE VIEW MEDJUAN2 AS SELECT ESPECIALIDADXMEDICO.ID_MEDICO ID,MEDICO.NOMBRE NOMBRE,MEDICO.APELLIDO APELLIDO,ESPECIALIDAD.NOMBRE ESPECIALIDAD FROM ESPECIALIDADXMEDICO,MEDICO,ESPECIALIDAD WHERE ESPECIALIDADXMEDICO.ID_MEDICO=1 AND MEDICO.ID=1 AND ESPECIALIDADXMEDICO.ID_ESPECIALIDAD=ESPECIALIDAD.ID;
CREATE VIEW SEG1 AS SELECT PACIENTE.NOMBRE NOMBRE,PACIENTE.APELLIDO APELLIDO,SEGURO.NOMBRE SEGURO FROM PACIENTE,SEGURO WHERE PACIENTE.SEGURO=SEGURO.ID;
CREATE VIEW CIT1 AS SELECT CITA.ID ID,CITA.FECHA_CITA FECHA,MEDICO.ID MEDICO_ID,MEDICO.NOMBRE MED_NOMBRE,MEDICO.APELLIDO MED_APELLIDO,PACIENTE.NOMBRE PAC_NOMBRE,PACIENTE.APELLIDO PAC_APELLIDO,CIUDAD.NOMBRE CIU_NOMBRE,CITA.ID_CONSULTORIO CON_ID FROM MEDICO,CITA,CIUDAD,PACIENTE WHERE CITA.ID_PACIENTE=PACIENTE.ID AND MEDICO.ID=CITA.ID_MEDICO;